        $this->assertEquals(3, $this->distance->distance('Weimar Hyperinflation', 'German Nationalism'));
        $this->assertEquals(3, $this->distance->distance('Weimar Hyperinflation', 'German Populism'));
        $this->assertEquals(3, $this->distance->distance('Weimar Hyperinflation', 'German Socialism'));
        $this->assertEquals(4, $this->distance->distance('Weimar Hyperinflation', 'Germany does not attack Russia'));
        $this->assertEquals(2, $this->distance->distance('Weimar Hyperinflation', 'Germany attacks Russia'));
        $this->assertEquals(3, $this->distance->distance('Weimar Hyperinflation', 'Russia declares war on Britain'));
        $this->assertEquals(4, $this->distance->distance('Weimar Hyperinflation', 'Germany does not honor alliance with Japan'));
        $this->assertEquals(2, $this->distance->distance('Weimar Hyperinflation', 'Germany declares war on US'));
        $this->assertEquals(5, $this->distance->distance('Weimar Hyperinflation', 'Decisive German Victory in WW II'));
        $this->assertEquals(5, $this->distance->distance('Weimar Hyperinflation', 'Marginal German Victory in WW II'));
        $this->assertEquals(4, $this->distance->distance('Weimar Hyperinflation', 'Marginal German Defeat in WW II'));
        $this->assertEquals(3, $this->distance->distance('Weimar Hyperinflation', 'Decisive German Defeat in WW II'));
        $this->assertEquals(6, $this->distance->distance('Weimar Hyperinflation', 'Germany builds the Bomb'));
        $this->assertEquals(6, $this->distance->distance('Weimar Hyperinflation', 'Germany abandons the Bomb'));
        $this->assertEquals(8, $this->distance->distance('Weimar Hyperinflation', 'US Obliterates Europe'));
        $this->assertEquals(8, $this->distance->distance('Weimar Hyperinflation', 'US Establishes Strategic Deterrent'));
        $this->assertEquals(8, $this->distance->distance('Weimar Hyperinflation', 'Germany Wins Cold War'));
        $this->assertEquals(8, $this->distance->distance('Weimar Hyperinflation', 'Germany Stalemates Cold War'));
        $this->assertEquals(8, $this->distance->distance('Weimar Hyperinflation', 'Germany Loses Cold War'));
        $this->assertEquals(8, $this->distance->distance('Weimar Hyperinflation', 'Limited Exchange'));
        $this->assertEquals(6, $this->distance->distance('Weimar Hyperinflation', 'Fuhrer is succeeded'));
        $this->assertEquals(4, $this->distance->distance('Weimar Hyperinflation', 'Fuhrer is assassinated'));
    }

