    private function changePlayerLocation(
        $player,
        $location
    ) {
        $eigenfunction = static::$em->getRepository('TRBundle:EigenFunction')->findOneByDescription($location);
        $location      = static::$em->getRepository('TRBundle:State')->findOneBy(
            [
                'game'          => $this->game,
                'eigenfunction' => $eigenfunction,
            ]
        );

        $player->location = $location;
        static::$em->persist($player);
        static::$em->flush();
    }
